import styled from 'styled-components';
import imgHasItem from '../../../assets/images/cart-full.svg'
import imgHasNoItem from '../../../assets/images/cart-empty.svg'

export const StyledShoppingCartIcon = styled.div`
    position: absolute;
    top: 5%;
    right: 2rem;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    width: 2rem;
    height: 2rem;
    background: url(${({ hasItem }) => hasItem ? imgHasItem : imgHasNoItem});
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    border: none;
    cursor: pointer;
    padding: 0;
    z-index: 10;
    &:focus {
        outline: none;
    }
`
